import React from 'react';
function Main(){
  function handleEditAvatarClick() {
    const editAvatarButton = document.querySelector('.profile__button-avatar');
    const editAvatarPopup = document.querySelector('.popup_type-avatar');
    editAvatarButton.addEventListener('click', () => editAvatarPopup.classList.add('popup_opened'));
  } 

  function handleEditProfileClick() {
    const editProfileButton = document.querySelector('.profile__button-edit');
    const editProfilePopup = document.querySelector('.popup_type-edit');
    editProfileButton.addEventListener('click', () => editProfilePopup.classList.add('popup_opened'));	
  }

  function handleAddPlaceClick() {
    const addPlaceButton = document.querySelector('.profile__button-add');
    const addPlacePopup = document.querySelector('.popup_type-cards');	
    addPlaceButton.addEventListener('click', () => addPlacePopup.classList.add('popup_opened'));
  }

  return(<main className="main">
  <section className="profile">
  <div className="profile__union">
    <div className="profile__avatar-change">
      <img
        src="<%=require('./images/avatar-kusto.png')%>"
        alt="аватар"
        className="profile__avatar"
      />
      <button
        className="profile__button-avatar"
        type="button"
        aria-label="Изменить аватар пользователя"
        onClick={handleEditAvatarClick}
      />
    </div>
    <div className="profile__info">
      <div className="profile__union">
        <h1 className="profile__info-name">Жак-Ив Кусто</h1>
        <button
          className="profile__button-edit"
          type="button"
          aria-label="Редактировать профиль"
          onClick={handleEditProfileClick}
        />
      </div>
      <p className="profile__info-job">Исследователь океана</p>
    </div>
  </div>
  <button
    className="profile__button-add"
    type="button"
    aria-label="Добавить карточку"
    onClick={handleAddPlaceClick}
  />
</section>
<section className="elements"></section>
</main>)

}

export default Main;
