import React from 'react';
import api from '../utils/Api';
function Main({isEditAvatarPopupOpen, isEditProfilePopupOpen, isAddPlacePopupOpen}){
  const [userName, setUserName] = React.useState('');
  const [userDescription, setUserDescription] = React.useState('');
  const [userAvatar, setUserAvatar] = React.useState('');

  React.useEffect(() => {
    Promise.resolve(api.getUserData())
      .then(res => {
        setUserName(res.name);
      })
      .catch((err) => console.log(err));
  });

  React.useEffect(() => {
    Promise.resolve(api.getUserData())
      .then(res => {
        setUserDescription(res.about);
      })
      .catch((err) => console.log(err));
  });

  React.useEffect(() => {
    Promise.resolve(api.getUserData())
      .then(res => {
        setUserAvatar(res.avatar);
      })
      .catch((err) => console.log(err));
  });

  return(<main className="main">
  <section className="profile">
  <div className="profile__union">
    <div className="profile__avatar-change">
      <img
        src={`${userAvatar}`}
        alt="аватар"
        className="profile__avatar"
      />
      <button
        className="profile__button-avatar"
        type="button"
        aria-label="Изменить аватар пользователя"
        onClick={isEditAvatarPopupOpen}
      />
    </div>
    <div className="profile__info">
      <div className="profile__union">
        <h1 className="profile__info-name">{userName}</h1>
        <button
          className="profile__button-edit"
          type="button"
          aria-label="Редактировать профиль"
          onClick={isEditProfilePopupOpen}
        />
      </div>
      <p className="profile__info-job">{userDescription}</p>
    </div>
  </div>
  <button
    className="profile__button-add"
    type="button"
    aria-label="Добавить карточку"
    onClick={isAddPlacePopupOpen}
  />
</section>
<section className="elements"></section>
</main>)

}

export default Main;
